changeSet.sql:
create temporary table species select taxon.id as parent, child.taxon_id as child from TaxonBase as taxon join TaxonNameBase as name on (taxon.name_id = name.id) join TaxonNode parent on (taxon.id = parent.taxon_id) join TaxonNode as child on (parent.id = child.parent_id) where name.rank_id = 775 and taxon.DTYPE = 'Taxon';
select id, url, title, artist, updated, created INTO OUTFILE '/tmp/images.txt' FIELDS TERMINATED BY '\t' LINES TERMINATED BY '\n' FROM
(select IFNULL(taxon.lsid_lsid,'') as id, IFNULL(part.uri,'') as url, IFNULL(media.titleCache,'') as title, IFNULL(agent.titleCache,'') as artist, IFNULL(IF(media.updated is NULL, DATE_FORMAT(media.created,"%Y-%m-%dT%TZ"),DATE_FORMAT(media.updated,"%Y-%m-%dT%TZ")),'') as updated, IFNULL(DATE_FORMAT(media.created,"%Y-%m-%dT%TZ"),'') as created from media  join mediarepresentation as rep on (media.id = rep.media_id) join mediarepresentationpart as part on (rep.id = part.representation_id) left join media_rights on (media.id = media_rights.Media_id) left join rights on (media_rights.rights_id = rights.id) left join agentbase as agent on (rights.agent_id = Agent.id) join descriptionelementbase_media as deb_media on (media.id = deb_media.media_id) join descriptionelementbase as element on(element.id = deb_media.DescriptionElementBase_id) join DescriptionBase as description on (element.indescription_id = description.id) join species on (description.taxon_id = species.child) join taxonbase as taxon on (species.parent = taxon.id)
union
select IFNULL(taxon.lsid_lsid,'') as id, IFNULL(part.uri,'') as url, IFNULL(media.titleCache,'') as title, IFNULL(agent.titleCache,'') as artist, IFNULL(IF(media.updated is NULL, DATE_FORMAT(media.created,"%Y-%m-%dT%TZ"),DATE_FORMAT(media.updated,"%Y-%m-%dT%TZ")),'') as updated, IFNULL(DATE_FORMAT(media.created,"%Y-%m-%dT%TZ"),'') as created  from media join mediarepresentation as rep on (media.id = rep.media_id) join mediarepresentationpart as part on (rep.id = part.representation_id)left join media_rights on (media.id = media_rights.Media_id) left join rights on (media_rights.rights_id = rights.id) left join AgentBase as agent on (rights.agent_id = Agent.id) left join descriptionelementbase_media as deb_media on (media.id = deb_media.media_id) left join descriptionelementbase as element on (element.id = deb_media.DescriptionElementBase_id) left join DescriptionBase as description on (element.indescription_id = description.id) left join taxonbase as taxon on (description.taxon_id = taxon.id)) as images;
