changeSet.createProcedure:
CREATE PROCEDURE populateDate()
BEGIN          
  DECLARE dt DATE;
  SET @i = 0;
  REPEAT
    SET dt = adddate('2011-08-01',@i);
    INSERT INTO Time VALUES (datediff(dt,'2011-08-01'),year(dt),quarter(dt),month(dt),monthname(dt),week(dt),weekday(dt),dayname(dt));
    SET @i = @i + 1;
    UNTIL @i > 365
  END REPEAT;
END;

